package uz.gita.appbuilderuser.data.room.daoimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Queryimport androidx.room.Updateimport androidx.room.Upsertimport kotlinx.coroutines.flow.Flowimport uz.gita.appbuilderuser.data.room.entity.ComponentEntity@Daointerface ComponentDao {    @Query("SELECT * FROM ComponentEntity")    fun getAllComponents(): Flow<List<ComponentEntity>>    @Insert(onConflict = OnConflictStrategy.REPLACE)    fun addComponent(componentEntity: ComponentEntity)}