package uz.gita.appbuilderuser.presenter.componentsimport androidx.compose.foundation.Imageimport androidx.compose.foundation.layout.Rowimport androidx.compose.foundation.layout.Spacerimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.clipimport androidx.compose.ui.res.painterResourceimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dpimport uz.gita.appbuilderuser.Rimport uz.gita.appbuilderuser.data.model.DrawsData@Composablefun DrawsComponent(    drawsData: DrawsData,) {    Row(        modifier = Modifier            .fillMaxWidth()            .padding(12.dp)            .clip(RoundedCornerShape(12.dp))    ) {        Text(            text = drawsData.value,            modifier = Modifier                .padding(10.dp)                .align(Alignment.CenterVertically)        )        Spacer(modifier = Modifier.weight(1f))        Image(            painter = painterResource(                id =                if (drawsData.state) R.drawable.baseline_done_all_24                else R.drawable.round_access_time_24            ), contentDescription = "",            modifier = Modifier                .padding(10.dp)                .align(Alignment.CenterVertically)        )    }}@Preview(showBackground = true)@Composablefun DrawsComponentPreview() {    DrawsComponent(DrawsData("", "Value", false))}