package uz.gita.appbuilderuser.presenter.componentsimport androidx.compose.foundation.layout.fillMaxWidthimport androidx.compose.foundation.layout.paddingimport androidx.compose.foundation.text.KeyboardOptionsimport androidx.compose.material3.ExperimentalMaterial3Apiimport androidx.compose.material3.Textimport androidx.compose.material3.TextFieldimport androidx.compose.runtime.Composableimport androidx.compose.runtime.getValueimport androidx.compose.runtime.mutableStateOfimport androidx.compose.runtime.rememberimport androidx.compose.runtime.setValueimport androidx.compose.ui.Modifierimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.input.KeyboardTypeimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.spimport uz.gita.appbuilderuser.data.model.ComponentsModel@OptIn(ExperimentalMaterial3Api::class)@Composablefun InputComponent(    data: ComponentsModel,    listener: (String) -> Unit) {    var value by remember {        mutableStateOf("")    }    TextField(        value = value,        onValueChange = {            value = it            listener.invoke(it)        },        modifier = Modifier            .fillMaxWidth()            .padding(16.dp),        label = { Text("Enter your text") },        placeholder = { Text(text = "PlaceHolder") },        singleLine = true,        textStyle = TextStyle(fontSize = 18.sp),        keyboardOptions = KeyboardOptions(            keyboardType = when (data.type) {                "Email" -> KeyboardType.Email                "Number" -> KeyboardType.Number                else -> KeyboardType.Text            }        )    )}